/* My Scripts */
var header_height = $("nav").height(),
documentWidth = 0;

function scrollAnchor (object, source) {

	var object_hash = (source=='a') ? object.hash : object,
	target = $(object_hash),
	final_scroll = target.offset().top - header_height + 1;
	target = target.length ? target : $('[name=' + object.hash.slice(1) +']'); 

	if (target.length)
	{
		$('html,body').animate({
		 scrollTop: final_scroll
		}, 500, function(){
			$("#sidebar-wrapper").removeClass('active');
		});
	}
}

$(window).load(function(){
	
	documentWidth = $(window).width();
	
  $('a[href*=#]:not([href=#])').click(function(){
    if (location.pathname.replace(/^\//,'') == this.pathname.replace(/^\//,'') || location.hostname == this.hostname)
    {
      var dis = $(this);
			target = $(this.hash),
			final_scroll = target.offset().top - header_height + 1;
			target = target.length ? target : $('[name=' + this.hash.slice(1) +']');
			if (target.length && !dis.hasClass("fancybox"))
			{
				menu_clicked = 1;
				$('a[href*=#]:not([href=#])').removeClass('active');
				$('#menu-' + this.hash.slice(1)).addClass('active')
				
				$('html,body').animate({scrollTop: final_scroll}, 300, function(){
					setTimeout( function(){
						menu_clicked = 0;
						
						// hack for mobile fixed navbar menu, close when clicked
						if ( dis.parents(".navbar-collapse").hasClass("in") ){
							$(".navbar-toggle").click();
						}
					},100);
				});
				return false;
			}
    }
  });
    
});

$(window).resize(function(){
	documentWidth = $(window).width();
});

