	$("article .gallery .gallery-nav span").on('click', function(){
		var dis = $(this),
		direction = ( dis.hasClass('prev') ) ? 'prev' : 'next',
		total = $("article .gallery ul li").length,
		currentActive = $("article .gallery ul li.active");
		
		if ( direction == 'next' ){
			var hasMoreNext = ( currentActive.next('li').length > 0 ) ? 1 : 0;
		}
		if ( direction == 'prev' ){
			var hasMorePrev = ( currentActive.prev('li').length > 0 ) ? 1 : 0;
		}
				
		if ( !dis.hasClass('disabled') && (hasMorePrev||hasMoreNext) ){
			currentActive.removeClass('active');
			if ( direction == 'next' && currentActive.next('li').length > 0 ){
				currentActive = currentActive.next('li');
				currentActive.addClass('active');
				
				var hasMoreNext = ( currentActive.next('li').length > 0 ) ? 1 : 0;
				if ( !hasMoreNext ) dis.addClass('disabled');
			}
			if ( direction == 'prev' && currentActive.prev('li').length > 0 ){
				currentActive = currentActive.prev('li');
				currentActive.addClass('active');
				
				var hasMorePrev = ( currentActive.prev('li').length > 0 ) ? 1 : 0;
				if ( !hasMorePrev ) dis.addClass('disabled');
			}
			if ( currentActive.next('li').length > 0 )
				$("article .gallery .gallery-nav span.next").removeClass("disabled");
			if ( currentActive.prev('li').length > 0 )
				$("article .gallery .gallery-nav span.prev").removeClass("disabled");
			
		}
		
	});
	
